var picName = args[2];

const arr = krpano.get(picName+'picrure_array');
let number = krpano.get(picName + 'number');
const picture = krpano.get(`hotspot["${picName}"]`);

if (mode === 'right') {
	number++;
	if(number === arr.length) {
		number = 0;
	}
	krpano.set(picName + 'number' , number);
};
if (mode === 'left') {
	number--;
	if(number === -1) {
		number = arr.length-1
	}
	krpano.set(picName + 'number' , number);
};
picture.url = arr[number];


